                return {
                    ok:false,
                    error:"That user does not exist."
                }
            }
            await client.user.update({
                where:{
                    id: loggedInUser.id,
                },
                data:{
                    following:{
                        connect:{
                            username,
                        }
                    }
                }
            });
            return {
                ok:true,
            };  
        })
    }
}

